import React,{Component} from 'react';
import Header from './Header';
import Description from './Description';
import Cycle from './Cycle';
import { Container, Row, Col } from 'react-grid-system';
import {Button,ButtonGroup,ButtonToolbar } from 'reactstrap';
import ToggleButton from 'react-toggle-button';
class Main extends Component{

  render(){
      return (
    <div style={{padding:"10px",background:"#000000"}}>
      <Row style={{color:"white",background:"black",padding:"20px"}}>

        <Col>
          <div> 
            {/* <marquee direction="right"><img src={worm} style={{height:"50px"}}/></marquee> */}
            <center><a href="#" style={{textDecoration:"none",color:"white",fontFamily:"timeburner",fontSize:"40px",justifyContent:"center"}} onClick = {() => this.Stage0()}><h1 style={{fontWeight:"bold"}}>Project-Based-ReactJS-Learning</h1></a></center>
          </div>
        </Col>
        <Col>
            <div align="right" style={{width:"100%",paddingLeft:"300px"}}><p>English</p><ToggleButton value={ this.state.value || false } onToggle={(value) => this.setState({value:!value,})} /> </div>
        </Col> 

      </Row>
      <Row>
          <Col md={15} style={{background:"#555555",padding:"15px"}}>

          </Col>   
          <Col md={4} style={{background:"#000000"}}>
          </Col>             
      </Row>   
    </div>
  );
      }
}

export default Main;
